function test(){userValue=document.getElementById("user").value;if(userValue.match(/^(?=.{3,20}$)(?![_.])(?![0-9])[a-zA-Z0-9._]+(?<![_.])$/)){textUser=""}else{textUser="نام کاربری باید با حروف الفبا شروع و حداقل سه کاراکتر  باشد"}document.getElementById("message-user").innerHTML=textUser;nationalCode=document.getElementById("code").value;var text;if(!/^\d{10}$/.test(nationalCode)){text="کد ملی باید ۱۰ رقم باشد"}else{var check=parseInt(nationalCode[9]);var sum=0;var i;for(i=0;i<9;++i){sum+=parseInt(nationalCode[i])*(10-i)}sum%=11;if(sum<2&&check==sum||sum>=2&&check+sum==11){text=""}else{text="کد ملی نا معتبر است"}}document.getElementById("message-code").innerHTML=text;phoneValue=document.getElementById("phone").value;phoneno=/^\(?([0]{1})\)?([9]{1})?([0-9]{9})$/;if(phoneValue.match(phoneno)){textPhone=""}else{textPhone="شماره نا معتبر است"}document.getElementById("phone-messsage").innerHTML=textPhone;inputPwd=document.getElementById("pwd").value;if(inputPwd.match(/^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,20}$/)){textPassword=""}else{textPassword="رمز عبور باید حداقل ۸ کاراکتر و شامل حروف کوچک بزرگ و عدد باشد"}document.getElementById("password-messsage").innerHTML=textPassword;confirmPwd=document.getElementById("confirm-pwd").value;if(inputPwd===confirmPwd){textConfirmPwd=""}else{textConfirmPwd="رمز عبور صحیح را وارد نمایید"}document.getElementById("confirm-messsage").innerHTML=textConfirmPwd}function cleardata(){document.getElementById("user").value=null;document.getElementById("code").value=null;document.getElementById("phone").value=null;document.getElementById("pwd").value=null;document.getElementById("confirm-pwd").value=null}